DROP DATABASE IF EXISTS mkulima;
CREATE DATABASE MKULIMA;
USE  MKULIMA;

DROP TABLE IF EXISTS beans_Trend;
CREATE TABLE beans_Trend(
  Month VARCHAR(9)
, Year INTEGER(4)
, Nairobi VARCHAR(7)
, Eldoret VARCHAR(7)
, Kisumu VARCHAR(7)
, Mombasa VARCHAR(7)
);



DROP TABLE IF EXISTS maize_Trend;
CREATE TABLE maize_Trend(
  Month VARCHAR(9)
, Year INTEGER(4)
, Nairobi NUMERIC(7,2)
, Eldoret VARCHAR(7)
, Mombasa VARCHAR(7)
, Kisumu NUMERIC(7,2)
);



DROP TABLE IF EXISTS millet_Trend;
CREATE TABLE millet_Trend(
  Month VARCHAR(9)
, Year INTEGER(4)
, Nairobi VARCHAR(7)
);



DROP TABLE IF EXISTS potatoes_Trend;
CREATE TABLE potatoes_Trend(
  Month VARCHAR(9) 
, Year INTEGER(4)
, Nairobi VARCHAR(7)
, Eldoret VARCHAR(7)
, Kisumu VARCHAR(7)
, Kitui VARCHAR(7)
);





DROP TABLE IF EXISTS sorghum_Trend;
CREATE TABLE sorghum_Trend(
  Month VARCHAR(9)
, Year INTEGER(4)
, Nairobi VARCHAR(7)
, Eldoret VARCHAR(7)
, Kisumu VARCHAR(7)
, Kitui VARCHAR(7)
, Mombasa VARCHAR(7)
);





DROP TABLE IF EXISTS crops_Data;
CREATE TABLE crops_Data(
  crop_ID INTEGER AUTO_INCREMENT
, crop_Name VARCHAR(12)
, max_PH FLOAT(2)
, min_PH FLOAT(2)
, effect_On_PH FLOAT(2)
, slope_nai FLOAT(6)
, gradient_nai FLOAT(6)
, slope_eld FLOAT(6)
, gradient_eld FLOAT(6)
, slope_kis FLOAT(6)
, gradient_kis FLOAT(6)
, slope_kit FLOAT(6)
, gradient_kit FLOAT(6)
, slope_mom FLOAT(6)
, gradient_mom FLOAT(6)
, PRIMARY KEY(crop_ID)

);

